function useScrapes() {
  const [scrapes, setScrapes] = useState({});

  useEffect(() => {
    fetch('http://localhost:4444/data')
      .then(results => results.json())
      .then(data => {
        setScrapes(data);
      });
  }, []);
}
export default function Page({ children }) {
  const scrapes = useScrapes();
  return (
    <ScrapeProvider
      value={{
        scrapes,
      }}
    >
      <div className="page">{children}</div>
    </ScrapeProvider>
  );
}
